import React, { useState } from 'react';
import './Chatbot.css';

const Chatbot = ({ addProduct }) => {
  const [userMessage, setUserMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);

  const handleUserMessageChange = (event) => {
    setUserMessage(event.target.value);
  };

  const handleUserMessageSubmit = (event) => {
    event.preventDefault();
    if (userMessage.trim() !== '') {
      const newChatHistory = [...chatHistory, { message: userMessage, isUser: true }];
      setChatHistory(newChatHistory);

      // Simulating response from the system
      const responseMessage = `You said: ${userMessage}`;
      const newProduct = {
        id: Date.now(), // Generate a unique ID for the new product
        name: `New Product ${newChatHistory.length}`,
        link: 'https://www2.hm.com/en_in/productpage.1045459018.html', // Change this to the actual product link
        imageSrc: 'https://lp2.hm.com/hmgoepprod?set=format%5Bwebp%5D%2Cquality%5B79%5D%2Csource%5B%2F6e%2F0c%2F6e0c8f1f35588912662b029e214c999658221630.jpg%5D%2Corigin%5Bdam%5D%2Ccategory%5B%5D%2Ctype%5BLOOKBOOK%5D%2Cres%5Bm%5D%2Chmver%5B1%5D&call=url%5Bfile%3A%2Fproduct%2Fmain%5D', // Change this to the actual image URL
      };
      addProduct(newProduct); // Add the new product to the top right

      const response = { message: responseMessage, isUser: false };
      setTimeout(() => {
        setChatHistory([...newChatHistory, response]);
        setUserMessage('');
      }, 1000); // Simulate delay for the system message
    }
  };

  return (
    <div className="chatbot">
      <div className="chat-history">
        {chatHistory.map((chat, index) => (
          <div key={index} className={`message ${chat.isUser ? 'user' : 'system'}`}>
            {chat.message}
          </div>
        ))}
      </div>
      <form onSubmit={handleUserMessageSubmit} className="user-input">
        <input
          type="text"
          value={userMessage}
          onChange={handleUserMessageChange}
          placeholder="Type your message..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default Chatbot;
