import React, { useState } from 'react';
import Chatbot from './Chatbot';
import Product from './Product';
import './App.css';

const App = () => {
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [products, setProducts] = useState([
    // { id: 1, name: 'Product 1', link: 'https://example.com/product1', imageSrc: 'product1.jpg', thumbsUpVisible: true, thumbsDownVisible: true },
    // { id: 2, name: 'Product 2', link: 'https://example.com/product2', imageSrc: 'product2.jpg', thumbsUpVisible: true, thumbsDownVisible: true }
  ]);

  const handleThumbsUp = (product) => {
    if (!selectedProducts.includes(product)) {
      setSelectedProducts([...selectedProducts, product]);
      setProducts(products.filter((p) => p.id !== product.id));
    }
  };

  const handleThumbsDown = (product) => {
    setProducts(products.filter((p) => p.id !== product.id));
  };

  const addProduct = (product) => {
    setProducts([...products, { ...product, thumbsUpVisible: true, thumbsDownVisible: true }]);
  };

  return (
    <div className="app-container">
      <Chatbot addProduct={addProduct} />
      <div className="right-section">
        <div className="upper-right">
          {products.map((product) => (
            <Product
              key={product.id}
              name={product.name}
              imageSrc={product.imageSrc}
              link={product.link}
              thumbsUpVisible={product.thumbsUpVisible}
              thumbsDownVisible={product.thumbsDownVisible}
              onThumbsUp={() => handleThumbsUp(product)}
              onThumbsDown={() => handleThumbsDown(product)}
            />
          ))}
        </div>
        <div className="lower-right">
          {selectedProducts.map((product) => (
            <Product
              key={product.id}
              name={product.name}
              imageSrc={product.imageSrc}
              link={product.link}
              buyNowVisible={true}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
