import React, { useState } from 'react';
import Chatbot from './components/Chatbot';
import Product from './components/Product';
import './App.css';

const App = () => {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [products, setProducts] = useState([
    { id: 1, name: 'Product 1', link: 'https://example.com/product1', imageSrc: 'product1.jpg' },
    { id: 2, name: 'Product 2', link: 'https://example.com/product2', imageSrc: 'product2.jpg' }
  ]);

  const handleThumbsUp = (product) => {
    setSelectedProduct(product);
    setProducts(products.filter((p) => p.id !== product.id));
  };

  const handleThumbsDown = (product) => {
    setProducts(products.filter((p) => p.id !== product.id));
  };

  const addProduct = (product) => {
    setProducts([...products, product]);
  };

  return (
    <div className="app-container">
      <Chatbot addProduct={addProduct} />
      <div className="right-section">
        <div className="upper-right">
          {products.map((product) => (
            <Product
              key={product.id}
              name={product.name}
              imageSrc={product.imageSrc}
              link={product.link}
              thumbsUpVisible={!selectedProduct}
              thumbsDownVisible={!selectedProduct}
              onThumbsUp={() => handleThumbsUp(product)}
              onThumbsDown={() => handleThumbsDown(product)}
            />
          ))}
        </div>
        <div className="lower-right">
          {selectedProduct && (
            <Product
              name={selectedProduct.name}
              imageSrc={selectedProduct.imageSrc}
              link={selectedProduct.link}
              thumbsUpVisible={false}
              thumbsDownVisible={false}
              buyNowVisible={true}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
