import React, { useState } from 'react';
import Chatbot from './components/Chatbot';
import Product from './components/Product';
import './App.css';

const App = () => {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [products, setProducts] = useState([
    { name: 'Product 1' },
    { name: 'Product 2' }
  ]);

  const handleThumbsUp = (product) => {
    setSelectedProduct(product);
  };

  const handleThumbsDown = (product) => {
    setProducts(products.filter((p) => p !== product));
  };

  const updateProducts = (newProducts) => {
    setProducts(newProducts);
  };

  return (
    <div className="app-container">
      <Chatbot updateProducts={updateProducts} />
      <div className="right-section">
        <div className="upper-right">
          {products.map((product, index) => (
            <Product
              key={index}
              name={product.name}
              onThumbsUp={() => handleThumbsUp(product)}
              onThumbsDown={() => handleThumbsDown(product)}
            />
          ))}
        </div>
        <div className="lower-right">
          {selectedProduct && <Product name={selectedProduct.name} />}
        </div>
      </div>
    </div>
  );
};

export default App;
